    "scripts": [ "\r\nfunction onLoad()\r\n  self.setInvisibleTo({\"Blue\", \"Green\", \"Red\", \"Grey\"})\r\nend\r\n\r\nfunction onMarineDrop(marine)\r\n  broadcastToAll(marine.getName() .. \" peeks around the corner\", \"Teal\")\r\n  instances.map.call(\"revealArea\",\r\n    {area=\"Area 1 behind corner\", color=\"Teal\"})\r\n\r\n  local objects = utils.getObjectsInZone(\r\n    {19.50, 2.00, 5.00},\r\n    {0.00, 0.00, 0.00},\r\n    {1.00, 4.00, 4.00},\r\n    utils.isInvisibleToken\r\n  )\r\n\r\n  for _, obj in ipairs(objects) do\r\n    if obj.getGUID() ~= self.getGUID() then\r\n      obj.destruct()\r\n    end\r\n  end\r\n\r\n  self.destruct()\r\nend\n", "\r\nfunction onLoad()\r\n  self.setInvisibleTo({\"Blue\", \"Green\", \"Red\", \"Grey\"})\r\nend\r\n\r\nfunction onMarineDrop(marine)\r\n  broadcastToAll(\"Monsters horde breaks trhough blockage!\", \"Teal\")\r\n\r\n  instances.map.call(\"revealArea\",\r\n    {area=\"Area 1 horde\", color=\"Teal\"})\r\n\r\n  local obstacles = utils.getObjectsInZone(\r\n    {16.50, 2.00, -9.00},\r\n    {0.00, 0.00, 0.00},\r\n    {1.00, 4.00, 2.00},\r\n    utils.isObstacleToken\r\n  )\r\n\r\n  for _, obj in ipairs(obstacles) do\r\n    obj.destruct()\r\n  end\r\n\r\n\r\n  local encounters = utils.getObjectsInZone(\r\n    {21.50, 2.00, -2.00},\r\n    {0.00, 0.00, 0.00},\r\n    {1.00, 4.00, 2.00},\r\n    utils.isInvisibleToken\r\n  )\r\n\r\n  for _, obj in ipairs(encounters) do\r\n    if obj.getGUID() ~= self.getGUID() then\r\n      obj.destruct()\r\n    end\r\n  end\r\n\r\n  self.destruct()\r\nend\r\n\n" ],
