    "scripts": [ "\r\nfunction onMarineDrop(marine)\r\n  if not instances.progress.call(\"check\", \"remote controller\") then\r\n    broadcastToAll(\"Nothing happens\", \"Teal\")\r\n    return\r\n  end\r\n\r\n  broadcastToAll(\"Wall opens exposing the vault!\", \"Teal\")\r\n\r\n  local objects = utils.getObjectsInZone(\r\n    {27.50, 2.00, 4.00},\r\n    {0.00, 0.00, 0.00},\r\n    {1.00, 4.00, 2.00},\r\n    function(o)\r\n      return utils.isToken(o) or utils.isMapTile(o) or utils.isCharacter(o)\r\n    end\r\n  )\r\n\r\n  for _, obj in ipairs(objects) do\r\n\t\tobj.setPosition(obj.getPosition() + Vector(-3, 0, 0))\r\n  end\r\n\r\n\tinstances.map.call(\"revealArea\",\r\n\t\t{area=\"Area 4 vault\", color=\"Teal\"})\r\n\r\n  local encounters = utils.getObjectsInZone(\r\n    {28.50, 2.00, 4.00},\r\n    {0.00, 0.00, 0.00},\r\n    {1.00, 4.00, 2.00},\r\n    utils.isEncounterToken\r\n  )\r\n\r\n  for _, obj in ipairs(encounters) do\r\n    if obj.getGUID() ~= self.getGUID() then\r\n      obj.destruct()\r\n    end\r\n  end\r\n\r\n  self.destruct()\r\nend\r\n\n" ],
