    "scripts": [ "\nlocal scoreTotal = 0\n\n\n-- Local methods declaration\nlocal flushObjects\n\nfunction onMarineDrop(marine)\r\n  local marineColor = utils.getColor(marine.getName())\r\n\r\n  Global.UI.setAttribute(\"encounterConfirmationPrompt\", \"text\", \"Activate emergency vent?\")\r\n  Global.UI.setAttribute(\"encounterConfirmButton\", \"onClick\", self.getGUID()..\"/onConfirmation\")\r\n  Global.UI.setAttribute(\"encounterDeclineButton\", \"onClick\", self.getGUID()..\"/onDecline\")\n\n  instances.vis.call(\"showInterface\", {color=marineColor, id=\"encounterConfirmationPanel\"})\n  broadcastToAll(marine.getName()..\" is prompted to use emergency vent\", \"Teal\")\nend\n\n\nfunction onConfirmation(player, value, id)\n  instances.vis.call(\"hideInterface\", {color=\"Red|Green|Blue|White\", id=\"encounterConfirmationPanel\"})\n  broadcastToAll(utils.getMarine(player.color).getName() .. \" activates vent\", \"Teal\")\n  scoreTotal = 0\n\n  local door1Shut, door1 = isDoorShut({22.00, 0, -2.00})\n  local door2Shut, door2 = isDoorShut({25.00, 0, -2.00})\n\n  -- Start area\n  if not isDoorShut({8.00, 1.24, 5.00}) then\n    printToAll(\"Start area is vented\", \"Teal\")\n    objectsToFlush = utils.getObjectsInZone(\n      {6.00, 2.00, 5.00},\n      {0.00, 0.00, 0.00},\n      {4.00, 4.00, 6.00},\n      isObjectToFlush)\n    flushObjects(objectsToFlush)\n  end\n\n  printToAll(\"Area 1 is vented\", \"Teal\")\n\n  -- Area 1 south corridor\n  local objectsToFlush = utils.getObjectsInZone(\n    {16.00, 2.00, -9.50},\n    {0.00, 0.00, 0.00},\n    {10.00, 4.00, 3.00},\n    isObjectToFlush)\n  flushObjects(objectsToFlush)\n\n  -- Area 1 north corridor\n  objectsToFlush = utils.getObjectsInZone(\n    {14.50, 2.00, 5.50},\n    {0.00, 0.00, 0.00},\n    {13.00, 4.00, 3.00},\n    isObjectToFlush)\n  flushObjects(objectsToFlush)\n\n  -- Area 1 east corridor\n  objectsToFlush = utils.getObjectsInZone(\n    {20.00, 2.00, -2.00},\n    {0.00, 0.00, 0.00},\n    {4.00, 4.00, 12.00},\n    isObjectToFlush)\n  flushObjects(objectsToFlush)\n\n  -- Area 2\n  if not isDoorShut({20.00, 1.24, -11.00}) then\n    printToAll(\"Area 2 is vented\", \"Teal\")\n    objectsToFlush = utils.getObjectsInZone(\n      {20.00, 2.00, -13.00},\n      {0.00, 0.00, 0.00},\n      {6.00, 4.00, 4.00},\n      isObjectToFlush)\n    flushObjects(objectsToFlush)\n  end\n\n  if not (door1Shut and door2Shut) then\n    local doorBreaks = false\n    if door1Shut then\n      doorBreaks = true\n      door1.destruct()\n    elseif door2Shut then\n      doorBreaks = true\n      door2.destruct()\n    end\n\n    if doorBreaks then\n      broadcastToAll(\"One door can't hold and breaks! Areas 3 and 5 are vented!\", \"Teal\")\n    else\n      broadcastToAll(\"Doors are open! Areas 3 and 5 are vented!\", \"Teal\")\n    end\n\n    -- Area 3 and 5\n    objectsToFlush = utils.getObjectsInZone(\n      {28.50, 2.00, -3.00},\n      {0.00, 0.00, 0.00},\n      {13.00, 4.00, 10.00},\n      isObjectToFlush)\n    flushObjects(objectsToFlush)\n\n    if not isDoorShut({30.00, 1.23, 2.00}) then\n      -- Area 4 as well\n      printToAll(\"Even area 4 is exposed... Damn you should learn to close doors\", \"Teal\")\n      objectsToFlush = utils.getObjectsInZone(\n        {28.50, 2.00, 4.50},\n        {0.00, 0.00, 0.00},\n        {9.00, 4.00, 5.00},\n        isObjectToFlush)\n      flushObjects(objectsToFlush)\n    end\n  end\n\n  local numberOfMarines = 0\n  for _, clr in ipairs(constants.allMarineColors) do\n    if Player[clr].seated or dbg.cfg.pretendAllMarinesSeated then\n      numberOfMarines = numberOfMarines + 1\n    end\n  end\n\n  if numberOfMarines == 0 then\n    printToAll(\"WTF! No marines are seated to divide \" .. scoreTotal .. \" score between them\", \"Teal\")\n  else\n    printToAll(\"Kill score of \" .. scoreTotal .. \" is divided between \" .. numberOfMarines .. \" marines\",\n      \"Teal\")\n  end\n\n  local scorePerMarine = math.floor(scoreTotal / numberOfMarines)\n  local remainder = scoreTotal - numberOfMarines * scorePerMarine\n\n  for _, clr in ipairs(constants.allMarineColors) do\n    if Player[clr].seated or dbg.cfg.pretendAllMarinesSeated then\n      if clr == player.color then\n        instances.score.call(\"changeScore\", {color=clr, delta=scorePerMarine+remainder})\n      else\n        instances.score.call(\"changeScore\", {color=clr, delta=scorePerMarine})\n      end\n    end\n  end\n\nend\n\n\nfunction onDecline(player, value, id)\n  instances.vis.call(\"hideInterface\", {color=\"Red|Green|Blue|White\", id=\"encounterConfirmationPanel\"})\n  broadcastToAll(utils.getMarine(player.color).getName() .. \" doesn't use it for now\", \"Teal\")\nend\n\nfunction isObjectToFlush(obj)\n  return utils.isCharacter(obj)\n    or utils.isPickableToken(obj)\n    or obj.getName() == \"Body Encounter\"\nend\n\nfunction flushObjects(objects)\n  for _, obj in ipairs(objects) do\n    if utils.isAboveMap(obj) then\n\n      if utils.isToken(obj) then\n        if obj.getName() == \"Yellow Key\" then\n          broadcastToAll(\"Yellow key is flushed out, you can't progress further\", \"Teal\")\n        end\n        obj.destruct()\n\n      elseif utils.isMonster(obj) then\n        local stats = utils.getMonsterStats(obj.getName())\n        scoreTotal = scoreTotal + stats.score\n        obj.destruct()\n\n      elseif utils.isMarine(obj) then\n        obj.call(\"defeatClick\") -- emulate click on defeat button\n\n      end\n    end\n  end\nend\n\nfunction isDoorShut(position)\n    local door = utils.getObjectAt(position, utils.isDoor)\n    return door and not string.find(door.getName(), \"Open\") or false, door\nend\n\n" ],
