    "scripts": [ "\r\nfunction onLoad()\r\n  self.setInvisibleTo({\"Blue\", \"Green\", \"Red\", \"Grey\"})\r\nend\r\n\r\nfunction onMarineDrop(marine)\r\n  local faceIndex = math.random(1,6) -- emulate green die throw\r\n  local keyBroken = false\r\n  if faceIndex == 1 then\r\n    -- range is 1\r\n    printToColor(\"Green die emulated: range 1\", \"White\", \"Teal\")\r\n    broadcastToAll(\"You're in luck! The key appears to be in working order.\", \"Teal\")\r\n  elseif faceIndex >= 5 then\r\n    -- range is 3\r\n    printToColor(\"Green die emulated: range 3\", \"White\", \"Teal\")\r\n    broadcastToAll(\"The security key appears to be broken\", \"Teal\")\r\n    keyBroken = true\r\n  else\r\n    -- range is 2\r\n    printToColor(\"Green die emulated: range 2\", \"White\", \"Teal\")\r\n    broadcastToAll(\"*chittering noise*\", \"Teal\")\r\n    keyBroken = true\r\n    instances.map.call(\"revealArea\", {area=\"Area 4 ambush\", color=\"Teal\"})\r\n  end\r\n\r\n  if keyBroken then\r\n    local key = utils.getObjectAt({17.50, 1.26, 10.50}, function(obj)\r\n      return string.find(obj.getName(), \"Blue Key\")\r\n    end)\r\n    if key then\r\n      key.destruct()\r\n    end\r\n  end\r\n\r\n  self.destruct()\r\nend\r\n" ],
